import custom_axios from '../plugins/axios.js';
export default {
  props: {
    horario: {
      type: Object,
      default: () => ({
        id: "",
        id_materia: "",
        fh_desde: "",
        fh_hasta: ""
      })
    },
    editar: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      valid: false,
      horarioData: {
        id_materia: "",
        fh_desde: "",
        fh_hasta: ""
      },
      fechaDesde: "",
      horaDesde: "",
      fechaHasta: "",
      horaHasta: "",
      materias: [],
      materiaRules: [v => !!v || 'La materia es requerida'],
      desdeRules: [v => !!v || 'La fecha y hora de inicio son requeridas'],
      hastaRules: [v => !!v || 'La fecha y hora de fin son requeridas'],
      menuDesde: false,
      menuHasta: false
    };
  },
  watch: {
    horario: {
      handler(nuevoValor) {
        this.horarioData = {
          ...nuevoValor
        };
        if (this.horarioData.fh_desde) {
          const desde = new Date(this.horarioData.fh_desde);
          this.fechaDesde = this.formatDate(desde);
          this.horaDesde = this.formatTime(desde);
        }
        if (this.horarioData.fh_hasta) {
          const hasta = new Date(this.horarioData.fh_hasta);
          this.fechaHasta = this.formatDate(hasta);
          this.horaHasta = this.formatTime(hasta);
        }
        if (!this.editar) {
          this.resetValidation();
        }
      },
      deep: true
    }
  },
  methods: {
    formatDate(date) {
      if (!date) return null;
      const d = new Date(date);
      return `${d.getFullYear()}-${String(d.getMonth() + 1).padStart(2, '0')}-${String(d.getDate()).padStart(2, '0')}`;
    },
    formatTime(date) {
      if (!date) return null;
      const d = new Date(date);
      return `${String(d.getHours()).padStart(2, '0')}:${String(d.getMinutes()).padStart(2, '0')}:${String(d.getSeconds()).padStart(2, '0')}`;
    },
    updateDesde() {
      this.horarioData.fh_desde = `${this.fechaDesde}T${this.horaDesde}`;
    },
    updateHasta() {
      this.horarioData.fh_hasta = `${this.fechaHasta}T${this.horaHasta}`;
    },
    submit() {
      this.$refs.form.validate();
      if (this.valid) {
        if (this.editar) {
          this.editarHorario();
        } else {
          this.guardarHorario();
        }
      }
    },
    guardarHorario() {
      custom_axios.post('/apiv1/horariomateria', this.horarioData).then(response => {
        this.$emit('guardar');
        this.$emit('cerrar');
      }).catch(error => {
        console.error('Error guardando horario:', error);
      });
    },
    editarHorario() {
      custom_axios.patch(`/apiv1/horariomateria/${this.horarioData.id}`, this.horarioData).then(response => {
        this.$emit('guardar');
        this.$emit('cerrar');
      }).catch(error => {
        console.error('Error editando horario:', error);
      });
    },
    cancelar() {
      this.$emit('cancelar');
    },
    resetValidation() {
      if (this.$refs.form) {
        this.$refs.form.resetValidation();
      }
    },
    obtenerMaterias() {
      custom_axios.get('/apiv1/materia').then(response => {
        this.materias = response.data;
      }).catch(error => {
        console.error('Error obteniendo materias:', error);
      });
    }
  },
  created() {
    this.obtenerMaterias();
    if (this.horario) {
      this.horarioData = {
        ...this.horario
      };
    }
  }
};
