import axios from 'axios'

axios.defaults.baseURL = 'http://localhost:8000'  // Asegúrate de que esta URL sea correcta
axios.defaults.withCredentials = true

// Si estás usando Vuex, puedes agregar un interceptor para manejar errores globalmente
axios.interceptors.response.use(
  response => response,
  error => {
    if (error.response.status === 401) {
      // Manejar error de autenticación (por ejemplo, redirigir a login)
    }
    return Promise.reject(error)
  }
)

export default axios
