import custom_axios from '../plugins/axios.js';
export default {
  props: {
    materia: {
      type: Object,
      default: () => ({
        id: "",
        nombre: "",
        cant_alumnos: "",
        id_carrera: "",
        id_profesor: ""
      })
    },
    editar: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      valid: false,
      materiaData: {
        nombre: "",
        cant_alumnos: "",
        id_carrera: null,
        id_profesor: null
      },
      nombreRules: [v => !!v || 'El nombre es obligatorio'],
      cantAlumnosRules: [v => !!v || 'La cantidad de alumnos es obligatoria'],
      carreras: [],
      profesores: []
    };
  },
  watch: {
    materia: {
      handler(nuevoValor) {
        this.materiaData = {
          ...nuevoValor
        };
        if (!this.editar) {
          this.resetValidationForAbm();
        }
      },
      immediate: true,
      deep: true
    }
  },
  methods: {
    submit() {
      if (this.editar) {
        this.editarMateria();
      } else {
        this.guardarMateria();
      }
    },
    guardarMateria() {
      custom_axios.post('/apiv1/materia', this.materiaData).then(response => {
        console.log(response);
        this.resetValidationForAbm();
        this.$emit('guardar');
      }).catch(error => {
        console.log(error);
      });
    },
    editarMateria() {
      custom_axios.patch(`/apiv1/materia/${this.materiaData.id}`, this.materiaData).then(response => {
        console.log(response);
        this.resetValidationForAbm();
        this.$emit('guardar');
      }).catch(error => {
        console.log(error);
      });
    },
    cancelar() {
      this.resetValidationForAbm();
      this.$emit('cancelar');
    },
    resetValidationForAbm() {
      if (this.$refs.form) {
        this.$refs.form.resetValidation();
      }
    },
    fetchCarreras() {
      custom_axios.get('/apiv1/carrera').then(response => {
        this.carreras = response.data;
      }).catch(error => {
        console.log(error);
      });
    },
    fetchProfesores() {
      custom_axios.get('/apiv1/profesor').then(response => {
        this.profesores = response.data.map(profesor => ({
          ...profesor,
          nombreCompleto: `${profesor.nombre} ${profesor.apellido}`
        }));
      }).catch(error => {
        console.log(error);
      });
    }
  },
  created() {
    this.fetchCarreras();
    this.fetchProfesores();
  }
};
