import "core-js/modules/es.array.push.js";
import custom_axios from '../plugins/axios.js';
export default {
  props: {
    aula: {
      type: Object,
      default: () => ({
        id: "",
        descripcion: "",
        ubicacion: "",
        cant_pcs: "",
        cant_proyector: "",
        es_climatizada: null,
        aforo: ""
      })
    },
    editar: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      valid: false,
      attemptedSubmit: false,
      aulaData: {
        descripcion: "",
        ubicacion: "",
        cant_pcs: "",
        cant_proyector: "",
        es_climatizada: null,
        aforo: ""
      },
      descripcionRules: [v => !!v || 'La descripción es obligatoria', v => v && v.length <= 100 || 'La descripción debe tener menos de 100 caracteres'],
      ubicacionRules: [v => !!v || 'La ubicación es obligatoria', v => v && v.length <= 100 || 'La ubicación debe tener menos de 100 caracteres'],
      cant_pcsRules: [v => !!v || 'La cantidad de PCs es obligatoria', v => v >= 0 || 'La cantidad debe ser un número positivo'],
      cant_proyectorRules: [v => !!v || 'La cantidad de proyectores es obligatoria', v => v >= 0 || 'La cantidad debe ser un número positivo'],
      es_climatizadaRules: [v => v !== null || 'Debes seleccionar si es climatizada o no'],
      aforoRules: [v => !!v || 'El aforo es obligatorio', v => v > 0 || 'El aforo debe ser mayor que cero']
    };
  },
  watch: {
    aula: {
      handler(nuevoValor) {
        this.aulaData = {
          ...nuevoValor
        };
        if (this.editar) {
          this.$nextTick(() => {
            if (this.$refs.form) {
              this.$refs.form.validate();
            }
          });
        }
      },
      immediate: true,
      deep: true
    }
  },
  methods: {
    submit() {
      this.attemptedSubmit = true;
      if (this.$refs.form.validate()) {
        if (this.editar) {
          this.editarAula();
        } else {
          this.guardarAula();
        }
      }
    },
    guardarAula() {
      custom_axios.post("/apiv1/aula", this.aulaData).then(response => {
        console.log('Aula guardada:', response.data);
        this.$emit("guardar", {
          aula: response.data,
          esNueva: true
        });
        if (this.$router.currentRoute.path !== '/aulas') {
          this.$router.push('/aulas');
        }
      }).catch(error => {
        console.error('Error al guardar el aula:', error);
        this.$emit("error", "Error al guardar el aula: " + (error.response?.data?.message || error.message));
      });
    },
    editarAula() {
      custom_axios.patch(`/apiv1/aula/${this.aula.id}`, this.aulaData).then(response => {
        console.log('Aula editada:', response.data);
        this.$emit("guardar", {
          aula: response.data,
          esNueva: false
        });
        if (this.$router.currentRoute.path !== '/aulas') {
          this.$router.push('/aulas');
        }
      }).catch(error => {
        console.error('Error al editar el aula:', error);
        this.$emit("error", "Error al editar el aula: " + (error.response?.data?.message || error.message));
      });
    },
    cancelar() {
      if (this.$router.currentRoute.path !== '/aulas') {
        this.$router.push('/aulas');
      }
    },
    resetForm() {
      this.aulaData = {
        descripcion: "",
        ubicacion: "",
        cant_pcs: "",
        cant_proyector: "",
        es_climatizada: null,
        aforo: ""
      };
      this.$refs.form.resetValidation();
    }
  },
  created() {
    if (this.aula) {
      this.aulaData = {
        ...this.aula
      };
    }
  }
};
