import custom_axios from '../plugins/axios.js';
export default {
  props: {
    horario: {
      type: Object,
      default: () => ({
        id: "",
        id_materia: "",
        fh_desde: "",
        fh_hasta: ""
      })
    },
    editar: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      valid: false,
      horarioData: {
        id_materia: "",
        fh_desde: "",
        fh_hasta: ""
      },
      materias: [],
      materiaRules: [v => !!v || 'La materia es requerida'],
      desdeRules: [v => !!v || 'La fecha de inicio es requerida'],
      hastaRules: [v => !!v || 'La fecha de fin es requerida'],
      menuDesde: false,
      menuHasta: false
    };
  },
  watch: {
    horario: {
      handler(nuevoValor) {
        this.horarioData = {
          ...nuevoValor
        };
        if (!this.editar) {
          this.resetValidation();
        }
      },
      deep: true
    }
  },
  methods: {
    submit() {
      // Validar el formulario antes de enviar
      this.$refs.form.validate();
      if (this.valid) {
        if (this.editar) {
          this.editarHorario();
        } else {
          this.guardarHorario();
        }
      }
    },
    guardarHorario() {
      custom_axios.post('/apiv1/horariomateria', this.horarioData).then(response => {
        this.$emit('guardar');
        this.$emit('cerrar');
      }).catch(error => {
        console.error('Error guardando horario:', error);
      });
    },
    editarHorario() {
      custom_axios.patch(`/apiv1/horariomateria/${this.horarioData.id}`, this.horarioData).then(response => {
        this.$emit('guardar');
        this.$emit('cerrar');
      }).catch(error => {
        console.error('Error editando horario:', error);
      });
    },
    cancelar() {
      this.$emit('cancelar');
    },
    resetValidation() {
      if (this.$refs.form) {
        this.$refs.form.resetValidation();
      }
    },
    obtenerMaterias() {
      custom_axios.get('/apiv1/materia').then(response => {
        this.materias = response.data;
      }).catch(error => {
        console.error('Error obteniendo materias:', error);
      });
    }
  },
  created() {
    this.obtenerMaterias();
    if (this.horario) {
      this.horarioData = {
        ...this.horario
      };
    }
  }
};
