import custom_axios from '../plugins/axios.js';
export default {
  props: {
    horario: {
      type: Object,
      default: () => ({
        id: "",
        id_materia: "",
        fh_desde: "",
        fh_hasta: ""
      })
    },
    editar: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      valid: false,
      horarioData: {
        id_materia: "",
        fh_desde: "",
        fh_hasta: ""
      },
      fechaDesde: null,
      horaDesde: null,
      fechaHasta: null,
      horaHasta: null,
      materias: [],
      materiaRules: [v => !!v || 'La materia es requerida'],
      fechaDesdeRules: [v => !!v || 'La fecha de inicio es requerida'],
      fechaHastaRules: [v => !!v || 'La fecha de fin es requerida'],
      horaRules: [v => !!v || 'La hora es requerida'],
      menuDesde: false,
      menuHasta: false,
      materiasLoading: false,
      materiasError: ''
    };
  },
  watch: {
    horario: {
      handler(nuevoValor) {
        this.horarioData = {
          ...nuevoValor
        };
        if (this.horarioData.fh_desde) {
          const desde = new Date(this.horarioData.fh_desde);
          this.fechaDesde = this.formatDate(desde);
          this.horaDesde = this.formatTime(desde);
        }
        if (this.horarioData.fh_hasta) {
          const hasta = new Date(this.horarioData.fh_hasta);
          this.fechaHasta = this.formatDate(hasta);
          this.horaHasta = this.formatTime(hasta);
        }
        if (!this.editar) {
          this.resetValidation();
        }
      },
      deep: true,
      immediate: true
    }
  },
  methods: {
    formatDate(date) {
      if (!date) return null;
      const d = new Date(date);
      return `${d.getFullYear()}-${String(d.getMonth() + 1).padStart(2, '0')}-${String(d.getDate()).padStart(2, '0')}`;
    },
    formatTime(date) {
      if (!date) return null;
      const d = new Date(date);
      return `${String(d.getHours()).padStart(2, '0')}:${String(d.getMinutes()).padStart(2, '0')}`;
    },
    updateFechaDesde(date) {
      this.fechaDesde = date;
      this.menuDesde = false;
    },
    updateFechaHasta(date) {
      this.fechaHasta = date;
      this.menuHasta = false;
    },
    updateDateTime() {
      this.horarioData.fh_desde = `${this.fechaDesde}T${this.horaDesde}:00`;
      this.horarioData.fh_hasta = `${this.fechaHasta}T${this.horaHasta}:00`;
    },
    submit() {
      if (this.$refs.form.validate()) {
        this.updateDateTime();
        if (this.editar) {
          this.editarHorario();
        } else {
          this.guardarHorario();
        }
      }
    },
    guardarHorario() {
      custom_axios.post('/apiv1/horariomateria', this.horarioData).then(response => {
        this.$emit('guardar');
        this.$emit('cerrar');
      }).catch(error => {
        console.error('Error guardando horario:', error);
      });
    },
    editarHorario() {
      custom_axios.patch(`/apiv1/horariomateria/${this.horarioData.id}`, this.horarioData).then(response => {
        this.$emit('guardar');
        this.$emit('cerrar');
        this.$emit('actualizar');
      }).catch(error => {
        console.error('Error editando horario:', error);
      });
    },
    cancelar() {
      this.$emit('cancelar');
    },
    resetValidation() {
      if (this.$refs.form) {
        this.$refs.form.resetValidation();
      }
    },
    obtenerMaterias() {
      this.materiasLoading = true;
      this.materiasError = '';
      custom_axios.get('/apiv1/materia').then(response => {
        this.materias = response.data;
      }).catch(error => {
        this.materiasError = 'Error obteniendo materias';
        console.error('Error obteniendo materias:', error);
      }).finally(() => {
        this.materiasLoading = false;
      });
    }
  },
  created() {
    this.obtenerMaterias();
    if (this.horario) {
      this.horarioData = {
        ...this.horario
      };
    }
  }
};
