import custom_axios from '../plugins/axios.js';
export default {
  props: {
    reserva: {
      type: Object,
      default: () => ({
        id: "",
        id_aula: "",
        fh_desde: "",
        fh_hasta: "",
        observacion: ""
      })
    },
    editar: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      valid: false,
      reservaData: {
        id_aula: "",
        fh_desde: "",
        fh_hasta: "",
        observacion: ""
      },
      fechaDesde: "",
      horaDesde: "",
      fechaHasta: "",
      horaHasta: "",
      aulas: [],
      aulaRules: [v => !!v || 'El aula es requerida'],
      fechaDesdeRules: [v => !!v || 'La fecha de inicio es requerida'],
      fechaHastaRules: [v => !!v || 'La fecha de fin es requerida'],
      horaRules: [v => !!v || 'La hora es requerida'],
      observacionRules: [],
      menuDesde: false,
      menuHasta: false,
      aulasLoading: false,
      aulasError: ''
    };
  },
  watch: {
    reserva: {
      handler(nuevoValor) {
        this.reservaData = {
          ...nuevoValor
        };
        if (this.reservaData.fh_desde) {
          const desde = new Date(this.reservaData.fh_desde);
          this.fechaDesde = this.formatDate(desde);
          this.horaDesde = this.formatTime(desde);
        }
        if (this.reservaData.fh_hasta) {
          const hasta = new Date(this.reservaData.fh_hasta);
          this.fechaHasta = this.formatDate(hasta);
          this.horaHasta = this.formatTime(hasta);
        }
        if (!this.editar) {
          this.resetValidation();
        }
      },
      deep: true,
      immediate: true
    }
  },
  methods: {
    formatDate(date) {
      if (!date) return null;
      const d = new Date(date);
      return `${d.getFullYear()}-${String(d.getMonth() + 1).padStart(2, '0')}-${String(d.getDate()).padStart(2, '0')}`;
    },
    formatTime(date) {
      if (!date) return null;
      const d = new Date(date);
      return `${String(d.getHours()).padStart(2, '0')}:${String(d.getMinutes()).padStart(2, '0')}`;
    },
    updateDateTime() {
      this.reservaData.fh_desde = `${this.fechaDesde}T${this.horaDesde}:00`;
      this.reservaData.fh_hasta = `${this.fechaHasta}T${this.horaHasta}:00`;
    },
    submit() {
      this.$refs.form.validate();
      if (this.valid) {
        this.updateDateTime();
        if (this.editar) {
          this.editarReserva();
        } else {
          this.guardarReserva();
        }
      }
    },
    guardarReserva() {
      custom_axios.post('/apiv1/reservaaula', this.reservaData).then(response => {
        this.$emit('guardar');
        this.$emit('cerrar');
      }).catch(error => {
        console.error('Error guardando reserva:', error);
      });
    },
    editarReserva() {
      custom_axios.patch(`/apiv1/reservaaula/${this.reservaData.id}`, this.reservaData).then(response => {
        this.$emit('guardar');
        this.$emit('cerrar');
        this.$emit('actualizar');
      }).catch(error => {
        console.error(error);
      });
    },
    cancelar() {
      this.$emit('cancelar');
    },
    resetValidation() {
      if (this.$refs.form) {
        this.$refs.form.resetValidation();
      }
    },
    obtenerAulas() {
      this.aulasLoading = true;
      this.aulasError = '';
      custom_axios.get('/apiv1/aula').then(response => {
        this.aulas = response.data;
      }).catch(error => {
        this.aulasError = 'Error obteniendo aulas';
        console.error('Error obteniendo aulas:', error);
      }).finally(() => {
        this.aulasLoading = false;
      });
    },
    focusInput(ref) {
      this.$nextTick(() => {
        if (this.$refs[ref]) {
          this.$refs[ref].focus();
        }
      });
    }
  },
  created() {
    this.obtenerAulas();
    if (this.reserva) {
      this.reservaData = {
        ...this.reserva
      };
    }
  }
};
